using System;
using System.Linq;
using System.Threading;
using System.Transactions;

namespace AdventOfCodeCS._2015
{
    public class Day18
    {
        public void Run()
        {
            var initial = Parse();
            var next = new bool[initial.Length];

            var current = initial;
            Console.WindowWidth = Width + 1;
            Console.WindowHeight = Width + 2;
            for (var i = 0; i < 100; i++)
            {
                Print(current, i);
                Step(current, next);

                (current, next) = (next, current);
            }

            Print(current, 100);
            Console.WriteLine(current.Where(c => c).Count());
        }

        private void Step(bool[] current, bool[] next)
        {
            // 1 2 3
            // 4 X 5
            // 6 7 8
            var pos1 = - Width - 1;
            var pos2 = - Width;
            var pos3 = - Width + 1;
            var pos4 = - 1;
            var pos5 = + 1;
            var pos6 = + Width - 1;
            var pos7 = + Width;
            var pos8 = + Width + 1;
            var neighborsOn = 0;
            var i = 0;
            var widthMinusOne = Width - 1;

            for (var y = 0; y < Width; y++)
            {
                for (var x = 0; x < Width; x++) {

                    neighborsOn = 0;
                    if (y > 0)
                    {
                        if (x > 0)
                        {
                            Check(pos1);
                        }
                        Check(pos2);
                        if (x < widthMinusOne)
                        {
                            Check(pos3);
                        }
                    }

                    if (x > 0)
                    {
                        Check(pos4);
                    }
                    if (x < widthMinusOne)
                    {
                        Check(pos5);
                    }
                    if (y < widthMinusOne)
                    {
                        if (x > 0)
                        {
                            Check(pos6);
                        }
                        Check(pos7);
                        if (x < widthMinusOne)
                        {
                            Check(pos8);
                        }
                    }

                    // A light which is on stays on when 2 or 3 neighbors are on, and turns off otherwise.
                    // A light which is off turns on if exactly 3 neighbors are on, and stays off otherwise.
                    if (current[i])
                    {
                        next[i] = neighborsOn == 2 || neighborsOn == 3;
                    }
                    else
                    {
                        next[i] = neighborsOn == 3;
                    }

                    i++;

                    pos1++;
                    pos2++;
                    pos3++;
                    pos4++;
                    pos5++;
                    pos6++;
                    pos7++;
                    pos8++;
                }
            }

            next[0] = true;
            next[Width-1] = true;
            next[^Width] = true;
            next[^1] = true;

            void Check(int index)
            {
                if (current[index])
                {
                    neighborsOn++;
                }
            }
        }


        private void Print(bool[] lights, int iteration)
        {
            Console.ResetColor();
            Console.SetCursorPosition(0,0);
            Console.WriteLine("Iteration: " + iteration);
            Console.BackgroundColor = ConsoleColor.Black;
            Console.ForegroundColor = ConsoleColor.White;
            var i = 0;
            for (var y = 0; y < 10; y++)
            {
                for (var x = 0; x < 10; x++)
                {
                    Console.Write(lights[y*Width +x] ? '█' : ' ');
                }
                Console.WriteLine();
            }

            Thread.Sleep(10);

            // for (var x = 0; x < Width; x++)
            // {
            //     for (var y = 0; y < Width; y++)
            //     {
            //         Console.Write(lights[i++] ? '█' : ' ');
            //     }
            //     Console.WriteLine();
            // }
            Console.ResetColor();
        }

        private bool[] Parse()
        {
            var lines = Input.Split('\n');
            Width = lines[0].Length;
            var parsed = new bool[lines.Length*Width];
            var index = 0;
            foreach (var line in lines)
            {
                for (var i = 0; i < line.Length; i++)
                {
                    parsed[index++] = line[i] == '#';
                }
            }

            return parsed;
        }

